const WORLD_SIZE = 1000;
const WORLD_HALF_SIZE = 500;
const PARTICLES_NUMBER = 10000;
let pointCloud;
let particles = [];

let texture;

function setupTHREE() {
  texture = new THREE.TextureLoader().load(
    "https://cdn.glitch.com/550f6519-569b-4223-a4b1-f8107e92a2d4%2Fparticle_texture.jpg?v=1615198675652"
  );

  //particles
  for (let i = 0; i < PARTICLES_NUMBER; i++) {
    let tParticle = new Particle()
      .setPosition(0, 0, 0)
      .setVelocity(random(-3, 3), random(-3, 3), random(-3, 3));
    particles.push(tParticle);
  }

  pointCloud = getPoints();
  scene.add(pointCloud);

  console.log(pointCloud.geometry.attributes.position);

  params = {
    drawCount: PARTICLES_NUMBER
  };
  let guiFolder = gui.addFolder("DrawCount");
  guiFolder
    .add(params, "drawCount")
    .min(0)
    .max(PARTICLES_NUMBER)
    .step(1)
    .listen();
}

function updateTHREE() {
  // update the particles
  for (let i = 0; i < particles.length; i++) {
    let p = particles[i];
    p.move();
    p.adjustVelocity(-0.01);
    //p.update();
  }

  // update the vertices of the points
  let posArray = pointCloud.geometry.attributes.position.array;
  for (let i = 0; i < particles.length; i++) {
    let p = particles[i];

    let index = i * 3;
    posArray[index + 0] = p.pos.x;
    posArray[index + 1] = p.pos.y;
    posArray[index + 2] = p.pos.z;
  }
  //pointCloud.geometry.setDrawRange(0, particles.length);
  pointCloud.geometry.setDrawRange(0, params.drawCount);
  pointCloud.geometry.attributes.position.needsUpdate = true;
}

function getPoints() {
  let vertices = [];
  /*
  for (let i = 0; i < PARTICLES_NUMBER * 3; i += 3) {
    vertices[i + 0] = random(-WORLD_HALF_SIZE, WORLD_HALF_SIZE);
    vertices[i + 1] = random(-WORLD_HALF_SIZE, WORLD_HALF_SIZE);
    vertices[i + 2] = random(-WORLD_HALF_SIZE, WORLD_HALF_SIZE);
  }
  */
  for (let i = 0; i < particles.length; i++) {
    let p = particles[i];

    let index = i * 3;
    vertices[index + 0] = p.pos.x;
    vertices[index + 1] = p.pos.y;
    vertices[index + 2] = p.pos.z;
  }

  const geometry = new THREE.BufferGeometry();
  geometry.setAttribute(
    "position",
    new THREE.Float32BufferAttribute(vertices, 3)
  );

  const material = new THREE.PointsMaterial({
    color: 0xffffff,
    map: texture,
    depthTest: false,
    blending: THREE.AdditiveBlending
  });
  const points = new THREE.Points(geometry, material);
  return points;
}

class Particle {
  constructor() {
    this.pos = createVector();
    this.vel = createVector();
    this.acc = createVector();

    this.scl = createVector(1, 1, 1);
    this.mass = this.scl.x * this.scl.y * this.scl.z;

    this.rot = createVector();
    this.rotVel = createVector();
    this.rotAcc = createVector();

    this.lifespan = 1.0;
    this.lifeReduction = random(0.005, 0.01);
    this.isDone = false;
  }
  setPosition(x, y, z) {
    this.pos = createVector(x, y, z);
    return this;
  }
  setVelocity(x, y, z) {
    this.vel = createVector(x, y, z);
    return this;
  }
  setRotationAngle(x, y, z) {
    this.rot = createVector(x, y, z);
    return this;
  }
  setRotationVelocity(x, y, z) {
    this.rotVel = createVector(x, y, z);
    return this;
  }
  setScale(w, h, d) {
    h = h === undefined ? w : h;
    d = d === undefined ? w : d;
    const minScale = 0.01;
    if (w < minScale) w = minScale;
    if (h < minScale) h = minScale;
    if (d < minScale) d = minScale;
    this.scl = createVector(w, h, d);
    this.mass = this.scl.x * this.scl.y * this.scl.z;
    return this;
  }
  move() {
    this.vel.add(this.acc);
    this.pos.add(this.vel);
    this.acc.mult(0);
  }
  adjustVelocity(amount) {
    this.vel.mult(1 + amount);
  }
  rotate() {
    this.rotVel.add(this.rotAcc);
    this.rot.add(this.rotVel);
    this.rotAcc.mult(0);
  }
  applyForce(f) {
    let force = f.copy();
    force.div(this.mass);
    this.acc.add(force);
  }
  reappear() {
    if (this.pos.z > WORLD_SIZE / 2) {
      this.pos.z = -WORLD_SIZE / 2;
    }
  }
  disappear() {
    if (this.pos.z > WORLD_SIZE / 2) {
      this.isDone = true;
    }
  }
  age() {
    this.lifespan -= this.lifeReduction;
    if (this.lifespan <= 0) {
      this.lifespan = 0;
      this.isDone = true;
    }
  }
  attractedTo(x, y, z) {
    let target = new p5.Vector(x, y, z);
    let force = p5.Vector.sub(target, this.pos);
    if (force.mag() < 100) {
      force.mult(-0.002 * random(1, 5));
    } else {
      force.mult(0.0001);
    }
    this.applyForce(force);
  }
}

///// p5.js /////

function setup() {
  let canvas = createCanvas(640, 480);
  canvas.parent("container-p5");
  canvas.hide();
  background(50);

  initTHREE();
}

function draw() {
  noLoop();
}

///// three.js /////

let container, stats, gui, params;
let scene, camera, renderer;
let time = 0;
let frame = 0;

function initTHREE() {
  // scene
  scene = new THREE.Scene();

  // camera (fov, ratio, near, far)
  camera = new THREE.PerspectiveCamera(
    60,
    window.innerWidth / window.innerHeight,
    0.5,
    5000
  );
  camera.position.z = 100;

  // renderer
  renderer = new THREE.WebGLRenderer();
  renderer.setClearColor("#111111");
  renderer.setPixelRatio(window.devicePixelRatio);
  renderer.setSize(window.innerWidth, window.innerHeight);

  // container
  container = document.getElementById("container-three");
  container.appendChild(renderer.domElement);

  // controls
  let controls = new THREE.OrbitControls(camera, renderer.domElement);

  // gui
  // https://davidwalsh.name/dat-gui
  gui = new dat.gui.GUI();

  // stats
  stats = new Stats();
  stats.showPanel(0); // 0: fps, 1: ms, 2: mb, 3+: custom
  container.appendChild(stats.dom);

  setupTHREE();

  // let's draw!
  animate();
}

function animate() {
  requestAnimationFrame(animate);
  stats.update();
  time = performance.now();
  frame++;

  updateTHREE();

  render();
}

function render() {
  renderer.render(scene, camera);
}

// event listeners
window.addEventListener("resize", onWindowResize);

function onWindowResize() {
  camera.aspect = window.innerWidth / window.innerHeight;
  camera.updateProjectionMatrix();
  renderer.setSize(window.innerWidth, window.innerHeight);
}

